      env: 'DB_USER',
    },
    password: {
      doc: 'Database password',
      format: String,
      default: '',
      env: 'DB_PASS',
    },
    host: {
      doc: 'Database host name/IP',
      format: '*',
      default: '127.0.0.1',
      env: 'DB_HOST',
    },
    port: {
      doc: 'Database port',
      format: 'port',
      default: 5432,
      env: 'DB_PORT',
    },
  },

  // JWT
  token: {
    secret: {
      doc: 'JWT Secret',
      format: '*',
      default: 'sheep',
      env: 'JWT_SECRET',
    },
  },

});

// Load environment dependent configuration
// const env = config.get('env');
// config.loadFile(`./config/${env}.json`);
// Perform validation
config.validate({
  allowed: 'strict',
});

module.exports = config;

